WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/26.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/26.0/apex.js")}

try {
	var response = sforce.apex.execute("MC4SF.Util", "loadCampaigns", {id: '{!MC4SF__MC_Campaign__c.MC4SF__MailChimp_ID__c}'});

	if (response != 'Success') {
		alert(response);
	} else {
		top.location.href = top.location.href;
	}
} catch (error) {
	if (error.faultcode == 'sf:INSUFFICIENT_ACCESS') {
		alert('You do not have permission to perform this function.');
	} else {
		alert(error);
	}
}
